package org.tns.statickeyword;

class A
{
	int add(int a, int b) 
	{
		return a+b;
	}
	public static void display() 
	{
		System.out.println("Welcome");
	}
} 

public class DemoOnStaticMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			A.display();
	}

}
